import React, { useEffect, useState } from 'react';
import { useWeather } from './WeatherContext';
import { fetchWeatherData } from './WeaterAPI';
import Select from 'react-select';
import { fetchCityList } from './WeaterAPI';

const WeatherApp = () => {
  // useWeather hook'u ile WeatherContext'ten state ve dispatch'i alıyoruz.
  const { state, dispatch } = useWeather();

  // Select yapısı verileri için state
  const [cityList, setCityList] = useState([]);
  const [selectedCity, setSelectedCity] = useState('');

  useEffect(() => {
    // OpenWeatherMap API'den şehir listesini çek
    const fetchCities = async () => {
      try {
        // fetchCityList fonksiyonunu içe aktarın (WeaterAPI.js'den)
        const cities = await fetchCityList();
        setCityList(cities);
      } catch (error) {
        console.error('Şehir listesi alınırken hata oluştu:', error);
      }
    };

    fetchCities();
  }, []);

  const handleCityChange = async (selectedOption) => {
    const city = selectedOption.value;

    // Seçilen şehre göre hava durumu verilerini çek
    try {
      // API'den hava durumu verilerini çekmek için gerekli fonksiyonu çağırın
      const weatherData = await fetchWeatherData(city);
      dispatch({ type: 'SET_WEATHER_DATA', payload: weatherData });
    } catch (error) {
      console.error('Hava durumu verileri alınırken hata oluştu:', error);
    }

    // Seçilen şehri state'e kaydet
    setSelectedCity(city);
  };

  return (
    <div>
      <Select
        options={cityList.map((city) => ({ value: city.name, label: city.name }))}
        value={{ value: selectedCity, label: selectedCity }}
        onChange={handleCityChange}
        placeholder="Şehir Seçin"
      />

      {/* Hava durumu bilgilerini göstermek için gerekli bileşenleri ekleyin */}
    </div>
  );
};

export default WeatherApp;
