import axios from "axios";

const apiKey = "cf3d2f7cb1c203206734dde024bd1e10"

//Burada temel URL belirliyoruz. Temel URL'ye göre istekleri yapacağız.
const api = axios.create({
    baseURL: 'https://api.openweathermap.org/data/2.5',
  });

  export const fetchWeatherData = async (city) => {
    try {
      const response = await api.get('/forecast', {
        params: {
          q: city,
          appid: apiKey,
          units: 'metric', 
        },

        
      });
  
      console.log('Alınan Veri:', response.data);

      return response.data;
    } catch (error) {
      throw error;
    }
  };

  export const fetchCityList = async () => {
    try {
      // GitHub reposundan Türkiye'nin illeri, ilçeleri, semtleri ve mahalleleri içeren veri setini çekiyoruz.
      const response = await axios.get(
        "https://raw.githubusercontent.com/nejdetkadir/il-ilce-semt-mahalleler/master/il-ilce-semt-mahalle.json"
      );
  
      // Gelen veriyi uygun formata dönüştürme
      const turkishCities = response.data.map((city) => ({
        label: city.il, // İlin adı
        value: city.il, // İlin adını benzersiz bir değer olarak kullanabilirsiniz
      }));
  
      return turkishCities;
    } catch (error) {
      console.error("Şehir listesi alınırken hata oluştu:", error);
      throw error;
    }
  };
  
  

// API'nin /forecast endpoint'ine bir GET isteği gönderiyoruz.
