import axios from "axios";

const apiKey = "cf3d2f7cb1c203206734dde024bd1e10"

//Burada temel URL belirliyoruz. Temel URL'ye göre istekleri yapacağız.
const api = axios.create({
    baseURL: 'https://api.openweathermap.org/data/2.5',
  });

  export const fetchWeatherData = async (city) => {
    try {
      const response = await api.get('/forecast', {
        params: {
          q: city,
          appid: apiKey,
          units: 'metric', 
        },

        
      });
  
      console.log('Alınan Veri:', response.data);

      return response.data;
    } catch (error) {
      throw error;
    }
  };

  export const fetchCityList = async () => {
    try {
      const response = await axios.get('https://restcountries.com/v3.1/all?fields=name,subregion', {
        params: {
          subregion: 'Western Asia', // Türkiye'nin bulunduğu coğrafi bölgeye göre filtreleme
        },
      });
  
      const turkishCities = response.data
        .filter(country => country.subregion === 'Western Asia')
        .map(country => ({
          label: country.name.common,
          value: country.name.common,
        }));
  
      return turkishCities;
    } catch (error) {
      console.error('Şehir listesi alınırken hata oluştu:', error);
      throw error;
    }
  };
  
  

// API'nin /forecast endpoint'ine bir GET isteği gönderiyoruz.
